import React from 'react';
import Modal from '../modal/modal.component';

import IndicatorsList from '../indicators-list/indicators-list.component';

import { INDICATORS_TO_API } from '../../assets/constants';

import './add-stock-universe-button.styles.css';

class AddStockUniverseButton extends React.Component {

    constructor(props) {
        // console.log('constructor add')
        super(props);
        this.state = { 
            visible: false,
            universes: [
                {
                    id: 1,
                    name: 'SP500',
                    selected: true
                },
                {
                    id: 2,
                    name: 'NAS100',
                    selected: false
                }
            ]
        };
    }

    show = () => {
        this.setState(prevState => ({ 
            visible: true,
            universes: prevState.universes.map(item => ({
                ...item,
                selected: false
            }))
        }));
    }

    hide = () => {
        this.setState({ visible: false });
    }

    onToggle = event => {
        const updatedID = event.target.id;
        // const className = event.target.className;
        // const indicatorListName = className.replace('selected', '').trim();

        // console.log(updatedID,className,indicatorListName)

        this.setState(prevState => {


                const updated = prevState.universes.map(universe => {
                    // console.log(universe.id, updatedID)
                    if(universe.id === updatedID) {
                        const newS = {
                            ...universe,
                            selected: !universe.selected
                        }
                        console.log(newS,'news')
                        return newS;
                    }
                    else {
                        return universe;
                    }
                });
                // console.log(updated);

                return {
                universes: prevState.universes.map(universe => {
                    // console.log(universe.id, updatedID)
                    if(universe.id === updatedID) {
                        return {
                            ...universe,
                            selected: !universe.selected
                        }
                    }
                    else {
                        return universe;
                    }
                })
            }
        })
    }

    // handleOkCancel = (type, updatedState) => {
    //     this.hide();
    //     if(type === 'ok') {
    //         const columnNames = updatedState.usedIndicators.map(item => item.name);
    //         this.props.handleColumnUpdate(columnNames);
    //     }
    // }

    render() {
        // // console.log('ab',this.props)
        // const { usedIndicatorsDefault } = this.props;
        // // console.log(this.deriveIndicatorsArr(usedIndicatorsDefault))

        // const availableIndicatorsDefault = Object.keys(INDICATORS_TO_API).filter(value => !usedIndicatorsDefault.includes(value));
        // // console.log(availableIndicators,'k')

        return (
            <>
                <button 
                    onClick={this.show}
                    className="add-stock-universe-button" 
                    style={this.props.style}
                >
                    +
                </button>

                <Modal 
                    visible={this.state.visible} 
                    onClose={this.hide}
                    width={40}
                    height={30}
                    measure={'%'}
                    showCloseButton={true}
                    closeOnEsc={true}
                    closeMaskOnClick={false}
                    enterAnimation={'zoom'}
                    leaveAnimation={'zoom'}
                    duration={500}
                >
                    <IndicatorsList 
                        displayedIndicators={
                            this.state.universes
                        }
                        onToggle={this.onToggle}
                        className={'universes'}
                        headerName={'Universe'}
                        style={{width: '80%',marginTop: '20px'}}
                    />
                </Modal>
            </>
        )
    }
}

export default AddStockUniverseButton;
