import React from 'react';

import Select from './select.component';
import Dropdown from './dropdown.component';

import './radarscreen.styles.css';


const arr = [['SPX', '@ES', '@NQ', 'SPY'],['Daily', 'Daily', '5 Min', 'Hourly'],[3, 6, 8, 0]]

const header = ['Symbol', 'Interval', 'Price']

const intervals = ['Monthly', 'Weekly', 'Daily', '4 Hourly', 'Hourly', '30 Min', '5 Min', '1 Min']


const RadarScreen = () => {
	let i=0;

	return(
		<div className="radarscreen">
			<div className='space'>
			</div>



			{/* <div id="resizable">
			</div> */}
			<div id="grid-container">
				{header.map((value, colIdx) => {
					i++;
					return (
						<div 
								key={i} 
								id={i} 
								className='header'
								style={{ 
											gridRow: 1,
											gridColumn: colIdx+1}}
						>
							{value}
						</div>
					)
				})}
				{arr.map((value, colIdx) => value.map((rowVal,rowIdx) => {
						i++;
						// console.log('c',header[colIdx]);
						// header[colIdx] === 'Interval' ? console.log('int') : console.log('ni')
						if(header[colIdx] === 'Interval') {
							return (
								// <div className=""></div>
								<Dropdown 
									options={intervals}
									defaultValue='Daily'
									style={{ 
										gridRow: rowIdx+2,
										gridColumn: colIdx+1}}
									key={i} 
									id={i} 
									className='item'
								/> 
							)
						}
						else {
							return (
								<div 
									key={i} 
									id={i} 
									className='item'
									style={{ 
												gridRow: rowIdx+2,
												gridColumn: colIdx+1}}
								>
									{rowVal}
								</div>
							)
						}
					}
					)
				)}
			</div>
	</div>
	)
}

export default RadarScreen;
