import React from 'react';

import IndicatorsList from '../indicators-list/indicators-list.component';

import './indicator-selector.styles.css';

class IndicatorSelector extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            availableIndicators: [
                {name: 'SMA', id: 0, selected: false}, 
                {name: 'EMA', id: 1, selected: false}, 
                {name: 'Open', id: 2, selected: true},
                {name: 'Close', id: 3, selected: false}
            ],
            usedIndicators: [],
            availableIndicatorsSelected: [],
            usedIndicatorsSelected: []
        }
    }

    onToggle = (e) => {
        console.log(e.target.className,e.target.id)

        const index = e.target.id;

        this.setState(prevState => {
            const newSelect = !prevState.availableIndicators[index].selected
            console.log(prevState.availableIndicators[index],newSelect);

            console.log({
                ...prevState.availableIndicators[index],
                selected: newSelect
            });
                // return {
                //     availableIndicators: 
                // }
        })
    }

    render() {
        return (
            <div className="indicator-selector">
                <IndicatorsList 
                    displayedIndicators={this.state.availableIndicators}
                    onClick={this.onToggle}
                />

                <div className="add-remove">
                    <button>ADD</button>
                    <button>REMOVE</button>
                </div>

                <IndicatorsList displayedIndicators={this.state.usedIndicators}/>
            </div>

        )
    }
}

export default IndicatorSelector;
