import React from 'react';
import Modal from '../modal/modal.component';

import IndicatorsList from '../indicators-list/indicators-list.component';

import { INDICATORS_TO_API } from '../../assets/constants';

import './add-stock-universe-button.styles.css';

class AddStockUniverseButton extends React.Component {

    constructor(props) {
        // console.log('constructor add')
        super(props);
        this.state = { 
            visible: false,
            universes: [
                {
                    id: 1,
                    name: 'SP500',
                    selected: false
                },
                {
                    id: 2,
                    name: 'NAS100',
                    selected: false
                }
            ]
        };
    }

    show = () => {
        this.setState({ visible: true });
    }

    hide = () => {
        this.setState({ visible: false });
    }

    // deriveIndicatorsArr = indicators => indicators.map((indicator, index) => ({
    //         name: indicator, id: indicator, selected: false
    //     })
    // )


    // handleOkCancel = (type, updatedState) => {
    //     this.hide();
    //     if(type === 'ok') {
    //         const columnNames = updatedState.usedIndicators.map(item => item.name);
    //         this.props.handleColumnUpdate(columnNames);
    //     }
    // }

    render() {
        // // console.log('ab',this.props)
        // const { usedIndicatorsDefault } = this.props;
        // // console.log(this.deriveIndicatorsArr(usedIndicatorsDefault))

        // const availableIndicatorsDefault = Object.keys(INDICATORS_TO_API).filter(value => !usedIndicatorsDefault.includes(value));
        // // console.log(availableIndicators,'k')

        return (
            <>
                <button 
                    onClick={this.show}
                    className="add-stock-universe-button" 
                    style={this.props.style}
                >
                    +
                </button>

                <Modal 
                    visible={this.state.visible} 
                    onClose={this.hide}
                    width={60}
                    height={50}
                    measure={'%'}
                    showCloseButton={true}
                    closeOnEsc={true}
                    closeMaskOnClick={false}
                    enterAnimation={'zoom'}
                    leaveAnimation={'zoom'}
                    duration={500}
                >
                    <IndicatorsList 
                        displayedIndicators={
                            this.state.universes
                        }
                        // onToggle={}
                        className={'universes'}
                        headerName={'Universe'}
                        style={{width: '40%'}}
                    />
                </Modal>
            </>
        )
    }
}

export default AddStockUniverseButton;
