import React from 'react';

import Dropdown from './dropdown.component';

import { SYMBOLS, INTERVALS } from '../assets/constants';

import './radarscreen.styles.css';


const headerConst = ['Symbol', 'Interval', 'Price']

const selectTbl = {
	Symbol: SYMBOLS,
	Interval: INTERVALS
}

class RadarScreen extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			header: headerConst,
			Symbol: ['SPX', '@ES', '@NQ', 'SPY'],
			Interval: ['Daily', 'Daily', '5 Min', 'Hourly'],
			Price: [3, 6, 8, 0]
		}
	}

	onChange = (p, headerCol,i) => {
		console.log('change', this.state.header[headerCol],i);
	}

	render() {
	let i=0;

	const { header } = this.state;

		return(
			<div className="radarscreen">
				<div className='space'></div>

				<div id="grid-container">
					{
						header.map((value, colIdx) => {
							i++;
							return (
								<div 
									key={i} 
									id={i} 
									className='header'
									style={{ 
										gridRow: 1,
										gridColumn: colIdx+1}}
								>
									{value}
								</div>
							)
						})
					}

					{
						header.map((value, colIdx) => this.state[value].map((rowVal,rowIdx) => {
								i++;
								if(selectTbl[header[colIdx]] !== undefined) {
									return (
										<Dropdown 
											options={selectTbl[header[colIdx]]}
											defaultValue={selectTbl[header[colIdx]][0]}
											style={{ 
												gridRow: rowIdx+2,
												gridColumn: colIdx+1}}
											key={i} 
											id={i} 
											onChange={() => this.onChange(p, headerCol,i)}
										/> 
									)
								}
								else {
									return (
										<div 
											key={i} 
											id={i} 
											className='item'
											style={{ 
												gridRow: rowIdx+2,
												gridColumn: colIdx+1}}
										>
											{rowVal}
										</div>
									)
								}
							})
						) 
					}

				</div>
		</div>
		)
	}
}


export default RadarScreen;
