import React from 'react';

class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      time: new Date().toLocaleString()
    };
  }

  //method that updates
  tick() {
    this.setState({
      date: new Date().toLocaleString()
    });
  }

  render() {
    return (
      <p className="App-clock">
        The time is {this.state.time}.
      </p>
    );
  }
} 

export default Clock;
